import React from "react";
import { ListItem } from "../List";
import { Row, Col } from "../Grid";
import "./style.css";
// all above lines importing of react list itemfile
// and row and col from grid file importing the css


function Book({ title, subtitle, authors, link, description, image, Button }) {
  // creating function name book  with parameters  tittle, subtittle
  // aithors , link and description
  return (
    // returning function argument 
    <ListItem>

      <Row className="flex-wrap-reverse">
        <Col size="md-8">
          <h3 className="font-italic">{title}</h3>
          {subtitle && <h5 className="font-italic">{subtitle}</h5>}

        </Col>

        <Col size="md-4">
          <div className="btn-container">
            <a className="btn btn-light" target="_blank" rel="noopener noreferrer" href={link}>
              View
            </a>
            <Button />
          </div>
        </Col>
      </Row>
      <Row>
        <Col size="md-6">
          <p className="font-italic small">Written by {authors}</p>
        </Col>
      </Row>
      <Row>
        <Col size="12 sm-4 md-2">
          <img className="img-thumbnail img-fluid w-100" src={image} alt={title} />
        </Col>
        <Col size="12 sm-8 md-10">
          <p>{description}</p>

        </Col>
      </Row>
    </ListItem>
  );
}
// exporting  returning html function named as book

export default Book;
